import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const token = localStorage.getItem('token');
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    fetchBookings();
  }, [userId, token]);

  const fetchBookings = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/bookings/user/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setBookings(response.data);
    } catch (error) {
      console.error("Error fetching bookings", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = async (bookingId, startTime) => {
    const now = new Date();
    const start = new Date(startTime);
    const diff = (start - now) / (1000 * 60 * 60); // in hours
    
    if (diff <= 1) {
      setMessage('You can only cancel bookings more than 1 hour before start time.');
      setTimeout(() => setMessage(''), 3000);
      return;
    }
    
    if (!window.confirm('Are you sure you want to cancel this booking?')) return;
    
    try {
      await axios.delete(`http://localhost:5000/api/bookings/${bookingId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Booking cancelled successfully.');
      fetchBookings();
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to cancel booking');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const formatDateTime = (dateStr) => {
    const d = new Date(dateStr);
    return d.toLocaleDateString('en-GB') + ', ' + d.toLocaleTimeString('en-GB', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  if (loading) {
    return <div style={{ padding: '20px', textAlign: 'center' }}>Loading your bookings...</div>;
  }

  return (
    <div style={{ padding: '20px', maxWidth: '1000px', margin: '0 auto' }}>
      <h2 style={{ color: '#1976d2', marginBottom: '20px' }}>ðŸ“‹ My Bookings</h2>
      
      {message && (
        <p style={{
          color: message.includes('success') ? '#388e3c' : '#d32f2f',
          marginBottom: '20px',
          padding: '10px',
          borderRadius: '4px',
          backgroundColor: message.includes('success') ? '#e8f5e8' : '#fdeaea'
        }}>{message}</p>
      )}
      
      {bookings.length === 0 ? (
        <div style={{ textAlign: 'center', padding: '40px', color: '#666' }}>
          <div style={{ fontSize: '3rem', marginBottom: '16px' }}>ðŸ“…</div>
          <h3>No bookings found</h3>
          <p>You haven't made any room bookings yet.</p>
        </div>
      ) : (
        <div style={{ overflowX: 'auto' }}>
          <table style={{ width: '100%', borderCollapse: 'collapse', background: '#fff' }}>
            <thead>
              <tr style={{ backgroundColor: '#f2f2f2' }}>
                <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>Room</th>
                <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>Start Time</th>
                <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>End Time</th>
                <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>Status</th>
                <th style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>Action</th>
              </tr>
            </thead>
            <tbody>
              {bookings.map((booking) => (
                <tr key={booking._id}>
                  <td style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>
                    <strong>{booking.room?.name}</strong>
                  </td>
                  <td style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>
                    {formatDateTime(booking.startTime)}
                  </td>
                  <td style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>
                    {formatDateTime(booking.endTime)}
                  </td>
                  <td style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>
                    <span style={{
                      padding: '4px 8px',
                      borderRadius: '4px',
                      fontSize: '0.9rem',
                      backgroundColor: new Date(booking.startTime) > new Date() ? '#e8f5e8' : '#fff3cd',
                      color: new Date(booking.startTime) > new Date() ? '#2e7d32' : '#856404'
                    }}>
                      {new Date(booking.startTime) > new Date() ? 'Upcoming' : 'Completed'}
                    </span>
                  </td>
                  <td style={{ border: '1px solid #ddd', padding: '12px', textAlign: 'center' }}>
                    {new Date(booking.startTime) > new Date() && (
                      <button
                        onClick={() => handleCancel(booking._id, booking.startTime)}
                        style={{
                          padding: '6px 12px',
                          background: '#d32f2f',
                          color: '#fff',
                          border: 'none',
                          borderRadius: '4px',
                          cursor: 'pointer'
                        }}
                      >
                        Cancel
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default MyBookings;
