import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MyBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const token = localStorage.getItem('token');
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    fetchBookings();
    // eslint-disable-next-line
  }, [userId, token]);

  const fetchBookings = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/bookings/user/${userId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setBookings(response.data);
    } catch (error) {
      console.error("Error fetching bookings", error);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = async (bookingId, startTime) => {
    const now = new Date();
    const start = new Date(startTime);
    const diff = (start - now) / (1000 * 60 * 60); // in hours
    
    if (diff <= 1) {
      setMessage('You can only cancel bookings more than 1 hour before start time.');
      setTimeout(() => setMessage(''), 3000);
      return;
    }
    
    if (!window.confirm('Are you sure you want to cancel this booking?')) return;
    
    try {
      await axios.delete(`http://localhost:5000/api/bookings/${bookingId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Booking cancelled successfully.');
      fetchBookings();
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to cancel booking');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const formatDateTime = (dateStr) => {
    const d = new Date(dateStr);
    return d.toLocaleDateString('en-GB') + ', ' + d.toLocaleTimeString('en-GB', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(120deg, #f8fafc 60%, #e3e9f7 100%)',
      padding: '0',
      margin: '0'
    }}>
      <div style={{
        width: '100%',
        maxWidth: '1000px',
        margin: '40px auto',
        background: '#fff',
        borderRadius: '18px',
        boxShadow: '0 4px 24px rgba(30,64,175,0.10)',
        padding: '40px 48px',
        minHeight: 'calc(100vh - 120px)',
        boxSizing: 'border-box'
      }}>
        <h2 style={{
          marginBottom: '28px',
          fontSize: '2.2rem',
          display: 'flex',
          alignItems: 'center',
          gap: '10px',
          color: '#1976d2',
          fontWeight: 700
        }}>
          <span role="img" aria-label="bookings">ðŸ“‹</span> My Bookings
        </h2>
        
        {message && (
          <p style={{
            color: message.includes('success') ? '#388e3c' : '#d32f2f',
            fontWeight: 500,
            marginBottom: '18px',
            padding: '12px',
            borderRadius: '8px',
            backgroundColor: message.includes('success') ? '#e8f5e8' : '#fdeaea'
          }}>{message}</p>
        )}
        
        {loading ? (
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            height: '200px',
            fontSize: '18px',
            color: '#1976d2'
          }}>
            Loading your bookings...
          </div>
        ) : bookings.length === 0 ? (
          <div style={{ 
            textAlign: 'center', 
            padding: '40px',
            color: '#666'
          }}>
            <div style={{ fontSize: '3rem', marginBottom: '16px' }}>ðŸ“…</div>
            <h3 style={{ color: '#888', fontSize: '1.2rem' }}>No bookings found</h3>
            <p style={{ color: '#999' }}>You haven't made any room bookings yet.</p>
          </div>
        ) : (
          <div style={{
            overflowX: 'auto',
            borderRadius: '12px',
            boxShadow: '0 2px 12px rgba(30,64,175,0.08)',
            background: '#f8fafc'
          }}>
            <style>
              {`
                div[style*="overflow-x: auto"]::-webkit-scrollbar {
                  height: 14px;
                }
                div[style*="overflow-x: auto"]::-webkit-scrollbar-thumb {
                  background: #bfc9d1;
                  border-radius: 8px;
                }
                div[style*="overflow-x: auto"]::-webkit-scrollbar-track {
                  background: #f7f8fa;
                }
              `}
            </style>
            <table style={{
              width: '100%',
              borderCollapse: 'collapse',
              background: '#fff',
              borderRadius: '12px',
              overflow: 'hidden'
            }}>
              <thead>
                <tr style={{ backgroundColor: '#f2f2f2' }}>
                  <th style={cellHeader}>Room</th>
                  <th style={cellHeader}>Start Time</th>
                  <th style={cellHeader}>End Time</th>
                  <th style={cellHeader}>Status</th>
                  <th style={cellHeader}>Action</th>
                </tr>
              </thead>
              <tbody>
                {bookings.map((booking) => (
                  <tr key={booking._id} style={{ borderBottom: '1px solid #e3e7ee' }}>
                    <td style={cell}>
                      <div style={{ fontWeight: 'bold', color: '#1976d2' }}>
                        {booking.room?.name}
                      </div>
                    </td>
                    <td style={cell}>{formatDateTime(booking.startTime)}</td>
                    <td style={cell}>{formatDateTime(booking.endTime)}</td>
                    <td style={cell}>
                      <span style={{
                        padding: '4px 12px',
                        borderRadius: '20px',
                        fontSize: '0.8rem',
                        fontWeight: 'bold',
                        backgroundColor: new Date(booking.startTime) > new Date() ? '#e8f5e8' : '#fff3cd',
                        color: new Date(booking.startTime) > new Date() ? '#2e7d32' : '#856404'
                      }}>
                        {new Date(booking.startTime) > new Date() ? 'Upcoming' : 'Completed'}
                      </span>
                    </td>
                    <td style={cell}>
                      {new Date(booking.startTime) > new Date() && (
                        <button
                          onClick={() => handleCancel(booking._id, booking.startTime)}
                          style={{
                            padding: '8px 16px',
                            background: '#d32f2f',
                            color: '#fff',
                            border: 'none',
                            borderRadius: '6px',
                            fontWeight: 600,
                            cursor: 'pointer',
                            fontSize: '0.9rem',
                            transition: 'background 0.2s, transform 0.1s'
                          }}
                          onMouseOver={e => {
                            e.target.style.background = '#b71c1c';
                            e.target.style.transform = 'translateY(-1px)';
                          }}
                          onMouseOut={e => {
                            e.target.style.background = '#d32f2f';
                            e.target.style.transform = 'translateY(0)';
                          }}
                        >
                          Cancel
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

const cellHeader = {
  border: '1px solid #e3e7ee',
  padding: '16px',
  textAlign: 'center',
  fontSize: '1.08rem',
  fontWeight: 700,
  color: '#1a237e',
  background: '#f5f5f5'
};

const cell = {
  border: '1px solid #e3e7ee',
  padding: '14px',
  textAlign: 'center',
  fontSize: '1rem',
  background: '#fff'
};

export default MyBookings;
