// client/src/pages/Signup.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Signup = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [otp, setOtp] = useState('');
  const [loading, setLoading] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [otpLoading, setOtpLoading] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [countdown, setCountdown] = useState(0);
  const navigate = useNavigate();

  // Countdown timer for resend OTP
  React.useEffect(() => {
    let timer;
    if (countdown > 0) {
      timer = setTimeout(() => setCountdown(countdown - 1), 1000);
    }
    return () => clearTimeout(timer);
  }, [countdown]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Send OTP function
  const handleSendOTP = async () => {
    // Validate all required fields before sending OTP
    if (!formData.email || !formData.name || !formData.password) {
      setError('Please fill in all fields (name, email, and password) before sending OTP');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters long');
      return;
    }

    setOtpLoading(true);
    setError('');
    setMessage('');

    try {
      // Create unverified user account with all details
      const response = await axios.post('http://localhost:5000/api/auth/signup', {
        ...formData,
        role: 'Employee'
      });

      setMessage('OTP sent successfully! Please check your email.');
      setOtpSent(true);
      setCountdown(60); // 60 second countdown
      
    } catch (err) {
      if (err.response?.data?.message === 'User already exists and is verified') {
        setError('User already exists and is verified. Please login instead.');
      } else if (err.response?.data?.message === 'User already exists') {
        // If user exists but not verified, try to resend OTP
        try {
          await axios.post('http://localhost:5000/api/auth/resend-otp', { 
            email: formData.email 
          });
          setMessage('OTP sent successfully! Please check your email.');
          setOtpSent(true);
          setCountdown(60);
        } catch (resendErr) {
          setError(resendErr.response?.data?.message || 'Failed to send OTP');
        }
      } else {
        setError(err.response?.data?.message || 'Failed to send OTP');
      }
    } finally {
      setOtpLoading(false);
    }
  };

  // Verify OTP function
  const handleVerifyOTP = async () => {
    if (!otp || otp.length !== 6) {
      setError('Please enter a valid 6-digit OTP');
      return;
    }

    setLoading(true);
    setError('');
    setMessage('');

    try {
      await axios.post('http://localhost:5000/api/auth/verify-otp', {
        email: formData.email,
        otp
      });

      setMessage('‚úÖ OTP verified successfully!');
      setOtpVerified(true);
      
      // Auto-redirect to login after 2 seconds
      setTimeout(() => {
        navigate('/login', {
          state: {
            message: 'Account created successfully! Please login with your credentials.',
            email: formData.email
          }
        });
      }, 2000);

    } catch (err) {
      setError(err.response?.data?.message || 'Invalid OTP');
    } finally {
      setLoading(false);
    }
  };

  // Resend OTP function
  const handleResendOTP = async () => {
    setOtpLoading(true);
    setError('');
    setMessage('');

    try {
      await axios.post('http://localhost:5000/api/auth/resend-otp', { 
        email: formData.email 
      });
      setMessage('New OTP sent successfully! Please check your email.');
      setCountdown(60);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to resend OTP');
    } finally {
      setOtpLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!otpVerified) {
      setError('Please verify your email with OTP first');
      return;
    }

    // User is already signed up and verified, just redirect
    navigate('/login', {
      state: {
        message: 'Account created successfully! Please login with your credentials.',
        email: formData.email
      }
    });
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(120deg, #f0f4f8 60%, #c9e7fa 100%)',
      fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif',
      padding: '20px'
    }}>
      <div style={{
        maxWidth: '450px',
        width: '100%',
        background: 'rgba(255,255,255,0.97)',
        padding: '40px 32px',
        borderRadius: '16px',
        boxShadow: '0 8px 32px rgba(0,0,0,0.18)',
        backdropFilter: 'blur(2px)'
      }}>
        <h2 style={{
          textAlign: 'center',
          marginBottom: '30px',
          color: '#1976d2',
          fontWeight: 700,
          fontSize: '28px'
        }}>
          üëã Join INCOR Group
        </h2>

        <form onSubmit={handleSubmit}>
          {/* Name Field */}
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: 500, color: '#333' }}>
              Full Name: <span style={{ color: '#e74c3c' }}>*</span>
            </label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              disabled={otpSent}
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: '1px solid #d1d5db',
                fontSize: '16px',
                background: otpSent ? '#f0f0f0' : '#f8fafc',
                boxSizing: 'border-box'
              }}
            />
          </div>

          {/* Email Field with Send OTP Button */}
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: 500, color: '#333' }}>
              Email Address: <span style={{ color: '#e74c3c' }}>*</span>
            </label>
            <div style={{ display: 'flex', gap: '8px' }}>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                disabled={otpSent}
                style={{
                  flex: 1,
                  padding: '12px',
                  borderRadius: '8px',
                  border: '1px solid #d1d5db',
                  fontSize: '16px',
                  background: otpSent ? '#f0f0f0' : '#f8fafc',
                  boxSizing: 'border-box'
                }}
              />
              <button
                type="button"
                onClick={handleSendOTP}
                disabled={otpLoading || otpSent || !formData.email || !formData.name || !formData.password}
                style={{
                  padding: '12px 16px',
                  background: (otpLoading || otpSent || !formData.email || !formData.name || !formData.password) 
                    ? '#ccc' 
                    : 'linear-gradient(90deg, #28a745 60%, #1e7e34 100%)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '14px',
                  fontWeight: 600,
                  cursor: (otpLoading || otpSent || !formData.email || !formData.name || !formData.password) ? 'not-allowed' : 'pointer',
                  whiteSpace: 'nowrap'
                }}
              >
                {otpLoading ? 'Sending...' : otpSent ? '‚úì Sent' : 'Send OTP'}
              </button>
            </div>
          </div>

          {/* Password Field - BEFORE OTP verification */}
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: 500, color: '#333' }}>
              Password: <span style={{ color: '#e74c3c' }}>*</span>
            </label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              disabled={otpSent}
              placeholder="Enter your password (min 6 characters)"
              style={{
                width: '100%',
                padding: '12px',
                borderRadius: '8px',
                border: '1px solid #d1d5db',
                fontSize: '16px',
                background: otpSent ? '#f0f0f0' : '#f8fafc',
                boxSizing: 'border-box'
              }}
            />
          </div>

          {/* OTP Input Field - Only show after OTP is sent */}
          {otpSent && (
            <div style={{ marginBottom: '20px' }}>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: 500, color: '#333' }}>
                Enter OTP: <span style={{ color: '#e74c3c' }}>*</span>
              </label>
              <div style={{ display: 'flex', gap: '8px' }}>
                <input
                  type="text"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))}
                  placeholder="Enter 6-digit OTP"
                  maxLength="6"
                  disabled={otpVerified}
                  style={{
                    flex: 1,
                    padding: '12px',
                    borderRadius: '8px',
                    border: '2px solid #e1e5e9',
                    fontSize: '18px',
                    textAlign: 'center',
                    fontFamily: 'monospace',
                    letterSpacing: '4px',
                    background: otpVerified ? '#f0f0f0' : '#f8fafc',
                    boxSizing: 'border-box'
                  }}
                />
                <button
                  type="button"
                  onClick={handleVerifyOTP}
                  disabled={loading || otp.length !== 6 || otpVerified}
                  style={{
                    padding: '12px 16px',
                    background: (loading || otp.length !== 6 || otpVerified) 
                      ? '#ccc' 
                      : 'linear-gradient(90deg, #1976d2 60%, #0d47a1 100%)',
                    color: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    fontSize: '14px',
                    fontWeight: 600,
                    cursor: (loading || otp.length !== 6 || otpVerified) ? 'not-allowed' : 'pointer',
                    whiteSpace: 'nowrap'
                  }}
                >
                  {loading ? 'Verifying...' : otpVerified ? '‚úì Verified' : 'Verify'}
                </button>
              </div>
              
              {/* Resend OTP Button */}
              {!otpVerified && (
                <div style={{ marginTop: '10px', textAlign: 'center' }}>
                  <button
                    type="button"
                    onClick={handleResendOTP}
                    disabled={otpLoading || countdown > 0}
                    style={{
                      background: 'transparent',
                      border: 'none',
                      color: countdown > 0 ? '#ccc' : '#1976d2',
                      cursor: countdown > 0 ? 'not-allowed' : 'pointer',
                      fontSize: '14px',
                      textDecoration: 'underline'
                    }}
                  >
                    {otpLoading ? 'Sending...' : 
                     countdown > 0 ? `Resend OTP in ${countdown}s` : 'Resend OTP'}
                  </button>
                </div>
              )}
            </div>
          )}

          {/* Steps Indicator */}
          <div style={{ 
            marginBottom: '25px', 
            padding: '15px', 
            background: '#f8f9fa', 
            borderRadius: '8px',
            border: '1px solid #e3e7ee'
          }}>
            <h4 style={{ margin: '0 0 10px 0', color: '#1976d2', fontSize: '16px' }}>
              üìã Signup Steps:
            </h4>
            <div style={{ fontSize: '14px', color: '#666' }}>
              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                marginBottom: '5px',
                color: (formData.name && formData.email && formData.password) ? '#28a745' : '#666'
              }}>
                <span style={{ marginRight: '8px' }}>
                  {(formData.name && formData.email && formData.password) ? '‚úÖ' : '1Ô∏è‚É£'}
                </span>
                Fill in all details (name, email, password)
              </div>
              <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                marginBottom: '5px',
                color: otpSent ? '#28a745' : '#666'
              }}>
                <span style={{ marginRight: '8px' }}>
                  {otpSent ? '‚úÖ' : '2Ô∏è‚É£'}
                </span>
                Send OTP to your email
              </div>
              <div style={{ 
                display: 'flex', 
                alignItems: 'center',
                color: otpVerified ? '#28a745' : '#666'
              }}>
                <span style={{ marginRight: '8px' }}>
                  {otpVerified ? '‚úÖ' : '3Ô∏è‚É£'}
                </span>
                Verify OTP and complete signup
              </div>
            </div>
          </div>

          {/* Role Note */}
          <div style={{ 
            marginBottom: '25px', 
            padding: '12px', 
            background: '#e3f2fd', 
            borderRadius: '6px',
            border: '1px solid #90caf9'
          }}>
            <p style={{ 
              margin: 0, 
              color: '#1976d2', 
              fontSize: '14px',
              display: 'flex',
              alignItems: 'center',
              gap: '8px'
            }}>
              üë§ <strong>Note:</strong> You'll be registered as an Employee. Contact admin for role changes.
            </p>
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            disabled={!otpVerified}
            style={{
              width: '100%',
              padding: '15px',
              background: !otpVerified 
                ? '#ccc' 
                : 'linear-gradient(90deg, #1976d2 60%, #0d47a1 100%)',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '18px',
              fontWeight: 600,
              cursor: !otpVerified ? 'not-allowed' : 'pointer',
              marginBottom: '20px'
            }}
          >
            {otpVerified ? 'Complete Signup' : 'Please Complete All Steps Above'}
          </button>
        </form>

        {/* Success Message */}
        {message && (
          <div style={{
            padding: '12px',
            background: message.includes('‚úÖ') ? '#d4edda' : '#d1ecf1',
            color: message.includes('‚úÖ') ? '#155724' : '#0c5460',
            borderRadius: '6px',
            marginBottom: '15px',
            textAlign: 'center'
          }}>
            {message}
          </div>
        )}

        {/* Error Message */}
        {error && (
          <div style={{
            padding: '12px',
            background: '#f8d7da',
            color: '#721c24',
            borderRadius: '6px',
            marginBottom: '15px',
            textAlign: 'center'
          }}>
            {error}
          </div>
        )}

        {/* Login Link */}
        <p style={{
          textAlign: 'center',
          color: '#666',
          fontSize: '14px'
        }}>
          Already have an account?{' '}
          <span
            onClick={() => navigate('/login')}
            style={{
              color: '#1976d2',
              cursor: 'pointer',
              textDecoration: 'underline'
            }}
          >
            Login here
          </span>
        </p>
      </div>
    </div>
  );
};

export default Signup;
