import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminLandingPage = () => {
  const [announcements, setAnnouncements] = useState([]);
  const [polls, setPolls] = useState([]);
  const [todayBookings, setTodayBookings] = useState([]);
  const [recentIssues, setRecentIssues] = useState([]);
  const [calendarEvents, setCalendarEvents] = useState([]);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      const announcementsRes = await axios.get('http://localhost:5000/api/announcements/all');
      setAnnouncements(announcementsRes.data.slice(0, 3));

      const pollsRes = await axios.get('http://localhost:5000/api/polls/all');
      setPolls(pollsRes.data.slice(0, 2));

      const bookingsRes = await axios.get('http://localhost:5000/api/bookings', {
        headers: { Authorization: `Bearer ${token}` }
      });

      const today = new Date();
      const todayStr = today.toISOString().split('T')[0];

      const todaysBookings = bookingsRes.data.filter(booking => {
        const bookingDate = new Date(booking.startTime).toISOString().split('T')[0];
        return bookingDate === todayStr;
      });

      setTodayBookings(todaysBookings);

      const issuesRes = await axios.get('http://localhost:5000/api/issues/all', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setRecentIssues(Array.isArray(issuesRes.data) ? issuesRes.data.slice(0, 3) : []);

      const calendarRes = await axios.get('http://localhost:5000/api/calendar/all');
      const todaysEvents = calendarRes.data.filter(event => {
        const eventDate = new Date(event.date).toISOString().split('T')[0];
        return eventDate === todayStr;
      });
      setCalendarEvents(todaysEvents);

      setStats({
        totalBookings: bookingsRes.data.length || 0,
        todayBookings: todaysBookings.length || 0,
        totalIssues: Array.isArray(issuesRes.data) ? issuesRes.data.length : 0,
        totalPolls: pollsRes.data.length || 0
      });

    } catch (error) {
      console.error('Error fetching data:', error);
      setRecentIssues([]);
      setAnnouncements([]);
      setPolls([]);
      setTodayBookings([]);
      setCalendarEvents([]);
      setStats({
        totalBookings: 0,
        todayBookings: 0,
        totalIssues: 0,
        totalPolls: 0
      });
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', fontSize: '18px', color: '#1976d2' }}>Loading admin dashboard...</div>;
  }

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(120deg, #f8fafc 60%, #e3e9f7 100%)', padding: '20px' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{ background: '#fff', borderRadius: '16px', padding: '24px', marginBottom: '24px', boxShadow: '0 4px 20px rgba(0,0,0,0.08)' }}>
          <h1 style={{ fontSize: '2.5rem', color: '#1976d2', margin: '0 0 8px 0', fontWeight: '700' }}>Admin Dashboard üë®‚Äçüíº</h1>
          <p style={{ fontSize: '1.2rem', color: '#666', margin: 0 }}>
            Manage your organization - {new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}
          </p>
        </div>

        {/* Rest of the code remains same up to today's meetings... */}

        <div style={{ background: '#fff', borderRadius: '12px', padding: '24px', boxShadow: '0 4px 16px rgba(0,0,0,0.06)' }}>
          <h3 style={{ color: '#1976d2', marginBottom: '20px', fontSize: '1.5rem', display: 'flex', alignItems: 'center', gap: '8px' }}>üìÖ Today's Meetings</h3>
          {todayBookings.length === 0 ? (
            <p style={{ color: '#888' }}>No meetings scheduled for today</p>
          ) : (
            <>
              {todayBookings.map(booking => (
                <div key={booking._id} style={{ padding: '12px', border: '1px solid #e3e9ee', borderRadius: '8px', marginBottom: '12px', background: '#f8fafc' }}>
                  <div style={{ fontWeight: 'bold', color: '#333' }}>{booking.room?.name} - {booking.bookedBy?.name}</div>
                  <div style={{ color: '#666', fontSize: '0.9rem' }}>
                    {new Date(booking.startTime).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' })} -
                    {new Date(booking.endTime).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' })}
                  </div>
                </div>
              ))}
              <button onClick={() => navigate('/admin/view-bookings')} style={{ background: '#1976d2', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer' }}>View All Bookings</button>
            </>
          )}
        </div>

        {/* Repeat similar fix for other sections if needed */}
      </div>
    </div>
  );
};

export default AdminLandingPage;
