// client/src/pages/Signup.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Signup = () => {
  const [formData, setFormData] = useState({ name: '', email: '', password: '' });
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [loading, setLoading] = useState(false);
  const [otpLoading, setOtpLoading] = useState(false);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [countdown, setCountdown] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    let timer;
    if (countdown > 0) {
      timer = setTimeout(() => setCountdown(countdown - 1), 1000);
    }
    return () => clearTimeout(timer);
  }, [countdown]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSendOTP = async () => {
    if (!formData.email || !formData.name) {
      setError('Enter name and email before sending OTP');
      return;
    }
    setOtpLoading(true);
    setError('');
    setMessage('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/signup', {
        ...formData,
        role: 'Employee'
      });
      setMessage('OTP sent successfully! Check your email.');
      setOtpSent(true);
      setCountdown(60);
    } catch (err) {
      if (err.response?.data?.message === 'User already exists') {
        try {
          await axios.post('http://localhost:5000/api/auth/resend-otp', {
            email: formData.email
          });
          setMessage('OTP resent successfully!');
          setOtpSent(true);
          setCountdown(60);
        } catch (resendErr) {
          setError(resendErr.response?.data?.message || 'Resend OTP failed');
        }
      } else {
        setError(err.response?.data?.message || 'Error sending OTP');
      }
    } finally {
      setOtpLoading(false);
    }
  };

  const handleVerifyOTP = async () => {
    if (!otp || otp.length !== 6) {
      setError('Enter valid 6-digit OTP');
      return;
    }
    setLoading(true);
    setError('');
    setMessage('');
    try {
      await axios.post('http://localhost:5000/api/auth/verify-otp', {
        email: formData.email,
        otp
      });
      setOtpVerified(true);
      setMessage('âœ… OTP Verified! You can now set a password.');
    } catch (err) {
      setError(err.response?.data?.message || 'Invalid OTP');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!otpVerified || !formData.password) {
      setError('Complete all steps before signing up.');
      return;
    }
    navigate('/login', {
      state: {
        message: 'Signup successful! Please login.',
        email: formData.email
      }
    });
  };

  return (
    <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: 'linear-gradient(120deg, #f0f4f8 60%, #c9e7fa 100%)', fontFamily: 'Segoe UI', padding: '20px' }}>
      <div style={{ maxWidth: '450px', width: '100%', background: 'rgba(255,255,255,0.97)', padding: '40px 32px', borderRadius: '16px', boxShadow: '0 8px 32px rgba(0,0,0,0.18)', backdropFilter: 'blur(2px)' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '30px', color: '#1976d2' }}>ðŸ‘‹ Join INCOR Group</h2>

        <form onSubmit={handleSubmit}>
          {/* Step 1: Name */}
          <div style={{ marginBottom: '20px' }}>
            <label>Name:</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} required style={{ width: '100%', padding: '10px' }} />
          </div>

          {/* Step 2: Email */}
          <div style={{ marginBottom: '20px' }}>
            <label>Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} disabled={!formData.name} required style={{ width: '100%', padding: '10px' }} />
            <button type="button" onClick={handleSendOTP} disabled={!formData.name || !formData.email || otpSent} style={{ marginTop: '10px', padding: '10px' }}>
              {otpLoading ? 'Sending...' : otpSent ? 'âœ“ Sent' : 'Send OTP'}
            </button>
          </div>

          {/* Step 3: OTP */}
          {otpSent && (
            <div style={{ marginBottom: '20px' }}>
              <label>Enter OTP:</label>
              <input type="text" value={otp} onChange={(e) => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))} maxLength={6} required disabled={otpVerified} style={{ width: '100%', padding: '10px' }} />
              <button type="button" onClick={handleVerifyOTP} disabled={otpVerified || otp.length !== 6} style={{ marginTop: '10px', padding: '10px' }}>
                {loading ? 'Verifying...' : otpVerified ? 'âœ“ Verified' : 'Verify OTP'}
              </button>
            </div>
          )}

          {/* Step 4: Password */}
          {otpVerified && (
            <div style={{ marginBottom: '20px' }}>
              <label>Password:</label>
              <input type="password" name="password" value={formData.password} onChange={handleChange} minLength={6} required style={{ width: '100%', padding: '10px' }} />
            </div>
          )}

          {/* Step 5: Signup */}
          <button type="submit" disabled={!otpVerified || !formData.password} style={{ width: '100%', padding: '12px', background: '#1976d2', color: '#fff', border: 'none', borderRadius: '6px' }}>
            Signup
          </button>
        </form>

        {message && <p style={{ color: 'green', marginTop: '15px' }}>{message}</p>}
        {error && <p style={{ color: 'red', marginTop: '15px' }}>{error}</p>}
      </div>
    </div>
  );
};

export default Signup;
