import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const VerifyOTP = () => {
  const [otp, setOtp] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [resendLoading, setResendLoading] = useState(false);
  const [countdown, setCountdown] = useState(0);
  
  const location = useLocation();
  const navigate = useNavigate();
  
  // Get email from location state
  const email = location.state?.email;
  const userName = location.state?.userName;

  // Redirect if no email
  useEffect(() => {
    if (!email) {
      navigate('/signup');
    }
  }, [email, navigate]);

  // Countdown timer for resend button
  useEffect(() => {
    let timer;
    if (countdown > 0) {
      timer = setTimeout(() => setCountdown(countdown - 1), 1000);
    }
    return () => clearTimeout(timer);
  }, [countdown]);

  const handleVerifyOTP = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/api/auth/verify-otp', {
        email,
        otp
      });

      setMessage('Email verified successfully! Redirecting to login...');
      
      // Store user data
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('userId', response.data.user.id);
      localStorage.setItem('name', response.data.user.name);
      localStorage.setItem('email', response.data.user.email);
      localStorage.setItem('role', response.data.user.role);

      // Redirect based on role
      setTimeout(() => {
        const role = response.data.user.role.toLowerCase();
        if (role === 'superadmin') {
          navigate('/super-admin');
        } else if (role === 'admin') {
          navigate('/admin');
        } else {
          navigate('/employee');
        }
      }, 2000);

    } catch (err) {
      setError(err.response?.data?.message || 'OTP verification failed');
    } finally {
      setLoading(false);
    }
  };

  const handleResendOTP = async () => {
    setResendLoading(true);
    setMessage('');
    setError('');

    try {
      await axios.post('http://localhost:5000/api/auth/resend-otp', { email });
      setMessage('New OTP sent successfully! Please check your email.');
      setCountdown(60); // 60 second countdown
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to resend OTP');
    } finally {
      setResendLoading(false);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(120deg, #f0f4f8 60%, #c9e7fa 100%)',
      fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif',
      padding: '20px'
    }}>
      <div style={{
        maxWidth: '450px',
        width: '100%',
        background: 'rgba(255,255,255,0.97)',
        padding: '40px 32px',
        borderRadius: '16px',
        boxShadow: '0 8px 32px rgba(0,0,0,0.18)',
        backdropFilter: 'blur(2px)',
        textAlign: 'center'
      }}>
        <h2 style={{
          marginBottom: '20px',
          color: '#1976d2',
          fontWeight: 700,
          fontSize: '28px'
        }}>
          ðŸ“§ Verify Your Email
        </h2>
        
        <p style={{
          marginBottom: '30px',
          color: '#666',
          fontSize: '16px',
          lineHeight: '1.5'
        }}>
          We've sent a 6-digit verification code to<br />
          <strong>{email}</strong>
        </p>

        <form onSubmit={handleVerifyOTP}>
          <div style={{
            marginBottom: '25px'
          }}>
            <input
              type="text"
              value={otp}
              onChange={(e) => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))}
              placeholder="Enter 6-digit OTP"
              maxLength="6"
              required
              style={{
                width: '100%',
                padding: '15px',
                fontSize: '24px',
                textAlign: 'center',
                borderRadius: '8px',
                border: '2px solid #e1e5e9',
                fontFamily: 'monospace',
                letterSpacing: '8px',
                boxSizing: 'border-box',
                background: '#f8fafc'
              }}
            />
          </div>

          <button
            type="submit"
            disabled={loading || otp.length !== 6}
            style={{
              width: '100%',
              padding: '15px',
              background: (loading || otp.length !== 6) 
                ? '#ccc' 
                : 'linear-gradient(90deg, #1976d2 60%, #0d47a1 100%)',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              fontSize: '18px',
              fontWeight: 600,
              cursor: (loading || otp.length !== 6) ? 'not-allowed' : 'pointer',
              marginBottom: '20px'
            }}
          >
            {loading ? 'Verifying...' : 'Verify OTP'}
          </button>
        </form>

        {/* Resend OTP Button */}
        <div style={{ marginBottom: '20px' }}>
          <button
            onClick={handleResendOTP}
            disabled={resendLoading || countdown > 0}
            style={{
              background: 'transparent',
              border: 'none',
              color: countdown > 0 ? '#ccc' : '#1976d2',
              cursor: countdown > 0 ? 'not-allowed' : 'pointer',
              fontSize: '16px',
              textDecoration: 'underline'
            }}
          >
            {resendLoading ? 'Sending...' : 
             countdown > 0 ? `Resend OTP in ${countdown}s` : 'Resend OTP'}
          </button>
        </div>

        {message && (
          <div style={{
            padding: '12px',
            background: '#d4edda',
            color: '#155724',
            borderRadius: '6px',
            marginBottom: '15px'
          }}>
            {message}
          </div>
        )}

        {error && (
          <div style={{
            padding: '12px',
            background: '#f8d7da',
            color: '#721c24',
            borderRadius: '6px',
            marginBottom: '15px'
          }}>
            {error}
          </div>
        )}

        <p style={{
          color: '#666',
          fontSize: '14px',
          marginTop: '20px'
        }}>
          Didn't receive the email? Check your spam folder or click resend.
        </p>
      </div>
    </div>
  );
};

export default VerifyOTP;
